function saveScriptWithSource(sourceString) {
    var strippedSource = this.removeTimeStampComment(sourceString);
    debuger
    var scriptName = eval('(' + strippedSource + ')').name,
        fullSource,
        scriptPane = this.get('ObjectEditorScriptPane'),
        saved,
        tagsEntry = this.get('TagsEntry'),
        tags = '[]';

    if (tagsEntry) {
        tags = tagsEntry.textString;
    }
    fullSource = Strings.format('this.addScript(%s).tag(%s);', strippedSource, tags);

    saved = scriptPane.boundEval(fullSource);
    if (saved) {
        this.changeIndicator.indicateUnsavedChanges();
        this.updateLists();
        this.selectChangedContent(sourceString);
        this.target[scriptName].sourceString = strippedSource;
    } 
    scriptPane.onSaved(saved);
}

