function gradientColor(color) {
    var gradient = new lively.morphic.LinearGradient(
                [
                    {offset: 0, color: Color.white},
                    {offset: 0.05, color: Color.white.mixedWith(color,0.5)},
                    {offset: 0.1, color: color},
                    {offset: 1, color: color}
                ],
                'northSouth'
            );
            return gradient;
}

