2274.source:
function onKeyDown(evt) {
    //$super(evt);
    var text = this.getTextString();
    if (evt.keyCode === 13 && text) {
        this.history.push(text);
        this.historyPosition = this.history.length;
        if (text.slice(0,1) === "/") {
            this.owner.processCommand(text);
        } else {
            this.owner.sendMessage(text);
        }
        this.setTextString("");
    } else if (evt.keyCode === 38) {
        this.historyPosition--;
        if (this.historyPosition >= 0) {
            this.setTextString(this.history[this.historyPosition]);
        } else {
            this.historyPosition = 0;
        }
    } else if (evt.keyCode === 40) {
        this.historyPosition++;
        if (this.historyPosition < this.history.length) {
            this.setTextString(this.history[this.historyPosition]);
        } else if (this.historyPosition == this.history.length) {
            this.setTextString("");
        } else {
            this.historyPosition = this.history.length;
        }
    };
    //evt.stop();
}

