2387.source:
function initializeCommands() {
    this.commands = {
        'nick': function (name) {
            this.setUserName(name);
            this.showMessage("changed nick to " + name);
        },
        'names': function () {
            this.whiteboard.getConnectedUserNames();
        },
        'channel': function (channel) {
            this.whiteboard.setChannel(channel);
        },
        'chan': this.commands['channel'],
        'clear': function () {
            this.whiteboard.clear();
        },
        'activate': function () {
            this.whiteboard.activate();
        },
        'a': this.commands['activate'],
        'deactivate': function () {
            this.whiteboard.deactivate();
        },
        'd': this.commands['deactivate'],
        'color': function (r, g, b, a) {
            this.whiteboard.setColor(Color.rgba(r, g, b, a));
        },
        'c': this.commands['color'],
        'pencil': function (size) {
            this.whiteboard.setPencilSize(size);
        },
        'p': this.commands['pencil'],
        'pencilstyler': function () {
            this.pencilStyler.toggle();
        },
        'ps': this.commands['pencilstyler'],
        'chat': function () {
            this.toggle();
        },
        'help': function () {
            var helpText = "HELP :: (available commands)\n";
            helpText += "----------------------------\n\n"
            helpText += "/help\n :: prints this help text\n"
            helpText += "/nick <nickname>\n :: sets your nick name\n"
            helpText += "/names\n :: shows the users in your channel\n"
            helpText += "/chan <channel>\n :: sets the channel you are in\n"
            helpText += "/clear\n :: clears your whiteboard\n"
            helpText += "/activate\n :: takes your whiteboard online\n"
            helpText += "/deactivate\n :: takes your whiteboard offline\n"
            helpText += "/color <r> <g> <b> <a>\n :: sets the color of your pencil\n"
            helpText += "/pencil <size>\n :: sets the size of your pencil in pixels\n"
            helpText += "/pencilstyler\n :: toggles the pencil styler\n"
            helpText += "/chat\n :: toggles the chat pane\n"
            this.showMessage(helpText);
        },
        'h': this.commands['help'],
    };
}

