function deactivate() {
    if (this.active) {
        this.socket.emit('mouse', {
            message: {x: 0, y: 0, remove: true},
            me: false,
            broadcast: true,
        });
        this.disconnectSocket();
        this.stopStepping();
        this.oldSubmorphs = [];
        this.setBorderColor(Color.black);
        this.active = false;
        this.owner.submorphs.each(function (ea) {
            ea.isMouseIndicator && ea.remove();
        });
    }
}

