2722.source:
function drawLineFrom(stroke) {
    var line = this.submorphs.find(function (ea) {
        return stroke.identifier == ea.identifier
    })
    if (!line) {
        line = this.createStrokeShape();
        line.identifier = stroke.identifier;
        this.addMorph(line);
        line.setVertices([]);
        line.setBorderColor(Color.rgba(stroke.color.r, stroke.color.g, stroke.color.b, stroke.color.a));
        line.setBorderWidth(stroke.width);
        line.setBorderStyle(stroke.style);
    }
    var vector = stroke.vector;
    if (vector instanceof Array) {
        vector.each(function (ea) {
            line.setVertices(line.vertices().concat([pt(ea.x, ea.y)]));
        });
    } else {
        line.setVertices(line.vertices().concat([pt(vector.x, vector.y)]));
    }
    line.timestamp = stroke.time
}

