2730.source:
function initialSync(initialData) {
    var that = this;
    // sync new strokes and morphs
    this.submorphs.each(function (ea) {
        if (typeof(ea.identifier) === 'undefined') {
            ea.identifier = ea.id;
            if (ea.isStroke) {
                var color = {
                    r: ea.getBorderColor().r*255,
                    g: ea.getBorderColor().g*255,
                    b: ea.getBorderColor().b*255,
                    a: ea.getBorderColor().a
                };
                that.sync(
                    ea.shape.vertices(),
                    ea.timestamp,
                    ea.identifier,
                    color,
                    ea.getBorderWidth(),
                    ea.getBorderStyle()
                );
            } else {
                that.saveMorph(ea);
            }
        }
    });
    this.oldSubmorphs = this.submorphs.pluck('identifier');

    // pull strokes and morphs that are not already there
    var existingIds = this.submorphs.pluck('identifier');    
    initialData.each(function (ea) {
        if (existingIds.indexOf(ea.identifier) < 0) {
            if (ea.vector) {
                that.drawLineFrom(ea);
            }
            else {
                var m = that.deserializeMorph(ea.morph);
                ea.changes.each(function (each) {
                    m.applyChanges(each);
                })
                that.addMorph(m);
                m.setPosition(pt(ea.position.x, ea.position.y))
            }
        }
    });
    this.active = true
}

2734.source:
function lineTo(point, optTransform, optColor) {
    if (optTransform)
        point = this.transformPoint(point);

    var line = this.getCurrentShape();
    var color = optColor || Color.black;
    color && line.setBorderColor(color)

    line.setVertices(line.vertices().concat([point]))
    if (this.active) {
        line.identifier = line.identifier || line.id
        this.sync(point, undefined, line.id, {r: color.r*255, g: color.g*255, b: color.b*255, a: color.a}, line.getBorderWidth(), line.getBorderStyle());
    }
}

