function morphMenuItems() {
    items = $super();
    var itemString = "activate sync";

    if (this.active) itemString = "de" + itemString;
    items.push([itemString, (function () {this.toggleActivation()}).bind(this)]);
    items.push(["clear whiteboard", (function () {this.clear()}).bind(this)])
    items.push([
            "change channel", 
            function () { $world.prompt(
                    'Enter a channel name', 
                    function (input) {
                        this.setChannel(input);
                    }.bind(this),
                    this.getChannel())
            }.bind(this)]);
    var penString = " pencil styler";
    if (this.pencilStyler.isVisible()) {
        penString = "hide" + penString;
    } else {
        penString = "show" + penString;
    }
    items.push([penString, (function () {this.pencilStyler.toggle()}).bind(this)]);
    var chatString = " chat";
    if (this.chat.isVisible()) {
        chatString = "hide" + chatString;
    } else {
        chatString = "show" + chatString;
    }
    items.push([chatString, (function () {this.chat.toggle()}).bind(this)]);
    return items;
}

