function onMouseDown(evt) {
    if (evt.isLeftMouseButtonDown()) {
        var mup = $world.morphsContainingPoint(evt.mousePoint);

        for (var i = 0; i < mup.length; i++) {
            if (!(mup[i] instanceof lively.morphic.Path)) {
                if (mup[i] === this) 
                    break;
                mup[i].onMouseDown(evt);
                return false
            }
        }
        if (lively.morphic.World.current().firstHand().submorphs.length <= 0)
            this.startDrawing(evt);
        evt.stop();
    }
    else
        $super(evt)
}

