function getCurrentShape() {
    this.oldSubmorphs = this.submorphs.pluck('identifier');
    if (typeof(this.currentShape) === 'undefined') {
        this.currentShape = this.createStrokeShape();  
        this.oldSubmorphs.push(this.currentShape.id)       
