2838.source:
function setChannel(channelName) {
    this.channelName = channelName,
        that = this;

    // TODO: maybe we don't want this for proper offline/online switching reasons.
    this.submorphs.each(function (ea) {
        ea.identifier = undefined
    })

    if (this.active && this.socket) {
        this.submorphs.each(function (ea) {
            ea.remove();
        })

        this.socket.emit('channel', this.channelName, function(join, joinChannel, leave, leaveChannel, initialData) {
            if (join) {
                alertOK('Successfully joined channel ' + joinChannel + '.');
                that.initialSync(initialData);
            } 
            if (leave) {
                alertOK('Successfully left channel ' + leaveChannel + '.');
            }
        });
    }
}

2842.source:
function createStrokeShape() {
    var m = Morph.makePolygon([], 1, Color.black, null);
    m.setBorderWidth(this.getPencilSize());
    m.setBorderStyle(this.getPencilStyle());
    m.disableEvents();
    m.disableDropping();
    m.isStroke = true;
    return m;
}

2846.source:
function connectSocket() {
    var resource = 'socket.io',
        socketIOpath = '/socket.io.js',
        socketioURL = 'http://lively-kernel.org:4000/' + resource + socketIOpath,
        that = this,
        channel = this.getChannel();

    // load Socket.io library if required
    if (typeof(io) == "undefined") {
        var source = JSLoader.getSync(socketioURL);
        window.eval(source);
    }

    // connect to server 
    this.socket = io.connect('lively-kernel.org:4000', {
        'force new connection': true,
        'reconnection delay': 1000,
    });

    // auth/(dis)connect + channel join
    that.createSocketListeners();
    this.socket.on('connect', function() {
        that.socket.emit('nick', {message: that.chat.getUserName()});
        that.socket.emit('channel', that.getChannel(), function(join, joinChannel, leave, leaveChannel, initialData) {
            if (join) {
                alertOK('Successfully joined channel ' + joinChannel + '.');
                that.initialSync(initialData);
            } 
            if (leave) {
                alertOK('Successfully left channel ' + leaveChannel + '.');
            }
        });
    });
}

2850.source:
function sync(vertices, time, id, color, width, style) {
    if (this.active) {
        var stroke = {
            board: this.getChannel(),
            time: time,
            drawer: this.getDrawerID(),
            identifier: id,
            vector: vertices,
            color: color,
            width: width,
            style: style,
        };
        this.socket.emit('draw', {
            message: stroke,
            me: false,
            broadcast: true,
        });
    }
}

2854.source:
function deleteMorphWith(identifier) {
    var toDelete = this.submorphs.find(function (ea) {
        return (ea.identifier === identifier)
    })
    // remove toDelete from oldSubmorphs ary
    if (!toDelete) 
        return
    this.oldSubmorphs = this.oldSubmorphs.select(function (ea) {
        return toDelete.identifier !== ea;
    })
    toDelete.remove();
}

2858.source:
