    this.socket.emit('delete', {
        identifier: identifier,
        me: true,
        broadcast: true
    });
}

2862.source:
function createSocketListeners() {
    var that = this;

    that.socket.on('error', function(reason) {
        alert('Unable to connect socket.io on namespace ' + channel + ': ' + reason);
        that.deactivate();
    });

    // client (dis)connects
    this.socket.on('clientConnect', function (data) {
        that.chat.showMessage(data.message + ' connected');
    });
    this.socket.on('clientDisconnect', function (data) {
        that.chat.showMessage(data.message.nick + ' disconnected');
        that.owner.submorphs.each(function (ea) {
            if (ea.isMouseIndicator && ea.identifier === data.message.id) {
                ea.remove();
            }
        });
    });

    // ping pong example
    this.socket.on('pong', function (data) {
        alertOK(data.message);
    });

    this.socket.on('draw', function (data) {
        that.drawLineFrom(data.message);
    });

    this.socket.on('clear', function () {
        that.submorphs.invoke('remove');
    });

    this.socket.on('delete', function (data) {
        that.deleteMorphWith(data.message);
    });

    this.socket.on('morph', function (data) {
        that.addMorphFrom(data.message.id);
    });

    this.socket.on('chat', function (data) {
        that.chat.showMessage(data.message, data.nick);
    });

    this.socket.on('names', function (data) {
        that.chat.showMessage("Currently connected users:\n" + data.message);
    });

    this.socket.on('nick', function (data) {
        that.chat.showMessage(data.message.old + " changed its name to " + data.message.new);
    });

    this.socket.on('mouse', function (data) {
        that.updateMouseIndicators(data);
    });

    this.socket.on('change', function(data)  {
        that.applyChanges(data.message)
    })
}

