2874.source:
function deserializeMorph(JSONmorph) {
    var serializer = ObjectGraphLinearizer.forNewLivelyCopy();
    var jso = JSON.parse(JSONmorph.morph);
    var morph = serializer.deserializeJso(jso);
    morph.withAllSubmorphsDo(function() { 
        if (typeof this.setNewId == 'function') 
            this.setNewId(); 
    });
    return morph;
}

2898.source:
function applyChanges(diffL) {
    var self = this;
    Properties.own(diffL).each(function (ea) {
        var morphChange = lively.persistence.Serializer.deserialize(diffL[ea]);
        var morph = self.submorphs.find(function (m) {
            return (m.identifier === ea || m.id === ea)
        })
        Properties.own(morphChange.modified).each(function (prop) {
            morph.applyNestedProperty(morph, prop.split('::'), morphChange.modified[prop].oldValue)
            // refresh the render context
            if (morph.owner) morph.refreshRenderContext();
        })
    })
}

