2906.source:
function observeChanges() {
    var self = this;
    var diffL = {};
    var copyFunc = function () {
        self.prevState = self.fakeCopy();
    }
    if (typeof(this.prevState) !== 'undefined') {
        this.prevState.submorphs.each(function (sub) {
            if (sub.isStroke) return
            var match = self.submorphs.find(function (ea) {
                return (sub.derivationIds.last() === ea.id)
            })
            if (!match) return
            var tmpDiffList = sub.diffTo(match, ['showsHalos']);
            if (tmpDiffList && !tmpDiffList.isEmpty()) {
                diffL[match.id] = tmpDiffList[sub.id];
            };
            diffL[match.identifier || match.id] && diffL[match.identifier || match.id].flattenPropertyTree();
        })
        if (Properties.own(diffL).length > 0) {
            self.sendChange(diffL);
            copyFunc.bind(this).delay(0);
        }
    }
    else
        copyFunc.bind(this).delay(0);
}

