2918.source:
function updateMouseIndicators(data) {
    // if exists then move it
    var position = pt(data.message.x, data.message.y)
                        .subPt(this.getScrollBounds().topLeft())
                        .addPt(this.getPosition());
    var morph = this.owner.submorphs.find(function (ea) {
        return ea.identifier === data.message.id
    })

    if (!morph) {
        morph = this.createMouseIndicator(data.message.id, data.message.color);
        this.owner.addMorph(morph);
    } else {
        if (data.message.remove) {
            morph.remove();
            return;
        }
    }
    var extent = this.getExtent();
    if (position.x > extent.x)
        position.x = extent.x;
    else if (position.x < 0)
        position.x = 0;
    if (position.y > extent.y)
        position.y = extent.y;
    else if (position.y < 21)
        position.y = 21;
    morph.setPosition(position);
    var that = this;
    if (data.message.morphs) {
        data.message.morphs.each(function (ea) {
            if (ea.morph) 
                var subMorph = that.deserializeMorph(ea.morph);
            else
                var subMorph = that.submorphs.find(function (each) {
                    return ea.id === each.identifier;
                });
            morph.addMorphBack(subMorph);
            subMorph.setPosition(pt(ea.position.x,ea.position.y));
        });
    }
}

2922.source:
function handleMovingHand(point) {
    var point = this.transformPoint(point);
    var that = this;
    if (this.isDrawing) {
        this.lineTo(point, false, this.getColor());
    }
    if (this.active) {
        var data = {x: point.x, y: point.y};
        var submorphsOfHand = lively.morphic.World.current().firstHand().submorphs.slice(1);
        if (!this.enteredBefore && submorphsOfHand.length > 0) {
            this.enteredBefore = true;
            data.morphs = submorphsOfHand.map(function (ea) {
                var position = ea.getPosition();
                if (ea.identifier) {
                    return {id: ea.identifier, position: position};
                } else {
                    ea.identifier = ea.id;
                    if (ea.selectionMorph) {
                        disconnect(ea, "extent", ea.selectionMorph, "setExtent");
                        disconnect(ea, "_Position", ea.selectionMorph, "setPosition");
                        disconnect(ea, "_Rotation", ea.selectionMorph, "setRotation");
                        disconnect(ea, "_Scale", ea.selectionMorph, "setScale");
                    }
                    var serialized = ea.serialize();
                    if (ea.selectionMorph) {
                        connect(ea, "extent", ea.selectionMorph, "setExtent");
                        connect(ea, "_Position", ea.selectionMorph, "setPosition");
                        connect(ea, "_Rotation", ea.selectionMorph, "setRotation");
                        connect(ea, "_Scale", ea.selectionMorph, "setScale");                    
                    }
                    return {morph: serialized, identifier: ea.identifier, position: position};
                }
            });
        }
        else if(submorphsOfHand.length > 0) {
            data.isNewMorphPosition = true;
            data.ids = submorphsOfHand.map(function (ea) {return ea.identifier})
        }
        this.socket.emit('mouse', {
            message: data,
            me: false,
            broadcast: true,
        });
    }  
}

