function deleteSelectedScript() {
    if (this.scriptList.selection && this.target) {
        if (!this.target.hasOwnProperty(this.scriptList.selection)) return;
        delete this.target[this.scriptList.selection];
        this.updateLists();
        this.displayInitialScript();
