5122.source:
function disconnectConnection() {
    var selection = this.connectionList.selection;
    if (selection && 
    (typeof selection !== "string") &&
    this.target.attributeConnections.indexOf(selection[1]) > -1) {
        var c = this.connectionList.selection[1];
        disconnect(c.sourceObj, c.sourceAttrName, c.targetObj, c.targetMethodName);
        this.updateLists();
        this.displayInitialScript();
    } 
}

5125.source:
function updateLists() {
    this.scriptList.setList(this.sortedScriptNamesOfObj(this.target));
    var scriptListItems = this.scriptList.getList();
    scriptListItems.unshift("-- ALL --");
    this.scriptList.setList(scriptListItems);

    this.connectionList.setList(this.sortedConnectionNamesOfObj(this.target));
    var connectionListItems = this.connectionList.getList();
    connectionListItems.unshift("-- ALL --");
    this.connectionList.setList(connectionListItems);
}

5128.source:
function displayInitialScript() {
    if (this.scriptList.getList().size() > 1) {
        this.scriptList.selectAt(0);
    } else if (this.connectionList.getList().size() > 1) {
        this.connectionList.selectAt(0);
    } else {
        this.scriptList.selectAt(0);
    }
}

