5336.source:
function drawAreaChart(w, h) {
	w = w - 30;
	h = h - 25;

	var vis = new pv.Panel()
		.width(w)
		.height(h)
		.bottom(20)
		.left(20)
		.right(10)
		.top(5);

	var xMin = pv.min(this.data.collect(function(d) {
			return d instanceof Array ? d[0] : null;
		})),
		xMax = Math.max(
			this.data.length - 1,
			pv.max(this.data.collect(function(d) {
				return d instanceof Array ? d[0] : null;
			}))
		),
		yVals = this.data.collect(function(d) {
			return d instanceof Array ? d[1] : d;
		}),
		x = (this.xScale ?
			(this.xScale.hasOwnProperty('isWrapper') ? this.xScale(xMax) : this.xScale) :
			pv.Scale.linear(xMin, xMax)
		).range(0, w),
		y = (this.yScale ?
			(this.yScale.hasOwnProperty('isWrapper') ? this.yScale(pv.max(yVals)) : this.yScale) :
			pv.Scale.linear(
				pv.min(yVals.concat(0)),
				pv.max(yVals)
			)
		).range(0, h);

	vis.add(pv.Rule)
		.data(y.ticks())
		.bottom(y)
		.strokeStyle(function(d) { return d ? "#eee" : "#000"; })
	.anchor("left").add(pv.Label)
		.text(this.yFormat || y.tickFormat);

	vis.add(pv.Rule)
		.data(x.ticks().length < this.data.length ? x.ticks() : x.ticks(this.data.length))
		.visible(function(d) { return d != 0; })
		.left(x)
		.bottom(-5)
		.height(5)
	.anchor("bottom").add(pv.Label)
	    .text(this.xFormat || x.tickFormat);

	vis.add(pv.Area)
		.def('idx', 0)
		.data(this.data)
		.bottom(1)
		.left(function(d) {
			var idx = this.idx();

			if (d instanceof Array)
				idx = d[0];
			this.idx(idx + 1);

			return x(idx);
		})
		.height(function(d) {
			if (d instanceof Array)
				d = d[1];
			return y(d);
		})
		.fillStyle("rgb(121,173,210)")
	.anchor("top").add(pv.Line)
		.lineWidth(3);

	return vis;
}

