5368.source:
function addDataAndRender(data) {
    if (data == null) return;

    if ((typeof data == 'object') && !(data instanceof Array))
        data = [data.x, data.y];

    if (!this.data)
        this.data = [data];
    else {
        if (this.maxDataLength) {
            this.data = this.data.slice(
                this.data.length-this.maxDataLength,this.data.length);
        }
        this.data.push(data);
    }

    this.render();
}

5372.source:
function drawBarChart(w, h) {
	var vis = new pv.Panel()
		.width(w)
		.height(h);

	var yRange = pv.range(
		pv.min(this.data.concat(0)),
		pv.max(this.data), 5);

	var leftPadding = 15,
		each_w = Math.floor(((w - leftPadding) / this.data.length) * 4 / 5),
		each_p = Math.ceil(each_w / 4)
		each_h = h / 20;

	vis.add(pv.Rule)
		.data(yRange)
		.bottom(function(d) { return d * each_h + .5; })
	.add(pv.Label);

	vis.add(pv.Bar)
		.data(this.data)
		.width(each_w)
		.height(function(d) { return d * each_h; })
		.bottom(0)
		.left(function() { return this.index * (each_w + each_p) + each_p / 2 + leftPadding; })
		.event("mouseover", function() { console.log($A(arguments)); })
	.anchor("bottom").add(pv.Label);

	return vis;
}

5376.source:
function drawPieChart(w, h) {
	var vis = new pv.Panel()
		.width(w)
		.height(h);

	var max = Math.min(h, w),
		r = max / 2,
		a = pv.Scale.linear(0, pv.sum(this.data)).range(0, 2 * Math.PI);

	vis.add(pv.Wedge)
		.data(this.data.sort(pv.reverseOrder))
		.bottom((max + (h - max)) / 2)
		.left((max + (w - max)) / 2)
		.innerRadius(0)
		.outerRadius(r)
		.angle(a)
	.anchor("center").add(pv.Label)
		.visible(function(d) { return d > .15; })
		.textAngle(0);

	return vis;
}

