function setChartType(cType) {
	var chart = this;
	if (!chart.vis) {
		alert('Chart type could not be set. Chart is missing!');
		chart.render();
		return;
	}

	switch (cType.toLowerCase()) {
	case 'bar':
		chart.drawBarChart.asScriptOf(chart, 'draw');
		break;
	case 'pie':
		chart.drawPieChart.asScriptOf(chart, 'draw');
		break;
	case 'area':
		chart.drawAreaChart.asScriptOf(chart, 'draw');
		break;
	default:
		alert('The chart type "' + cType + '" is unknown!');
		return;
	}
        this.render()
}

