5468.source:
function connectSocket() {
    var resource = 'socket.io',
        socketIOpath = '/socket.io.js',
        socketioURL = 'http://lively-kernel.org:4001/' + resource + socketIOpath,
        self= this

    // load Socket.io library if required
    if (typeof(io) == "undefined") {
        var source = JSLoader.getSync(socketioURL);
        window.eval(source);
    }

    if(this.socket){
        this.disconnectSocket();
    }

    // connect to server 
    this.socket = io.connect('lively-kernel.org:4001', {
        'force new connection': true,
        'reconnection delay': 1000,
    });

    this.socket.on('connect', function() {
        alertOK("Ping connected");
    });
    this.socket.on('disconnect', function() {
        alert("Ping disconnected");
        self.socket = undefined;
    });

    this.prepareSocket();
}

5476.source:
function prepareSocket() {
    if(!this.socket)
        return;

    var self = this;

    this.socket.on("ping", function(data){
        self.socket.emit("pong", data);
    });

    this.socket.on("pong", function(data){
        self.pong(data);
    });

    this.socket.on("clients", function(data){
        self.setClientList(data);
    })
}

