function pong(data) {
    var delta = new Date() -this.lastPing;
    this.get("Output").textString += " - pong "+ delta +"ms\n";
    this.get("Visualisation").addDataAndRender(delta);
    if(this.pinging){
        this.ping.bind(this).delay(1);
    }
}

