function toggle() {
    if (this.visible) {
        this.hide();
        this.visible = false;
    } else {
        this.show();
        this.visible = true;
        this.get("InputField").focus();
    }
    if (this.pencilStyler.isVisible()) {
        // rearrange the pencil styler
        this.pencilStyler.show();
    }
}

