709.source:
function showMessage(txtAry) {
    var txtAry = typeof(arguments[0]) === 'string' ?
                    [{message: arguments[0], color: Color.black}] : txtAry,
        pane = this.get("ConsolePane").get("TextPane"),
        currentText = pane.getRichText(),
        newText = new lively.morphic.RichText(),
        newTextChunks,
        content = new lively.morphic.RichText();
    newText.initialize();
    content.initialize();

    newTextChunks = txtAry.collect(function (ea) {
        var chunk = new lively.morphic.TextChunk(ea.message);
        chunk.styleText({color: ea.color});
        return chunk;
    });
    newText.setTextChunks(newTextChunks);

    content.setTextChunks(
        currentText.getTextChunks()
        .concat(newText.getTextChunks())    
    );
    pane.setRichText(content);
    pane.scrollToBottom();
}

