function sendMessage(messageText) {
    if (this.whiteboard.active) {
        this.whiteboard.socket.emit('chat', {
            message: messageText,
            me: true,
            broadcast: true,
        });
    } else {
        this.showMessage("! You are not online !");   
    }
}

