        func.apply(this, command.slice(1));
    } else {
        this.showMessage("!ERROR :: command \"" + command[0] + "\" is not available");
    }
}

725.source:
function createChatListeners() {
    var socket = this.whiteboard.socket,
        that = this;

    socket.on('chat', function (data) {
        var color = Color.hsb(
            data.message.color.h,
            data.message.color.s,
            data.message.color.b
        );

        that.showMessage([
            {message: '<', color: Color.black},
            {message: data.message.nick, color: color},
            {message: '> ' + data.message.text, color: Color.black}
        ]);
    });

    socket.on('names', function (data) {
        var txtAry = [{message: "Currently connected users:\n[", color: Color.black}],
            newNick,
            comma = {
                message: ", ",
                color: Color.black
            };

        data.message.each(function (ea) {
            newNick = {
                message: ea.nick,
                color: Color.hsb(ea.color.h, ea.color.s, ea.color.b)
            };
            txtAry.push(newNick);
            txtAry.push(comma);
        });
        txtAry.pop(); // remove the last comma
        txtAry.push({message: ']', color: Color.black})
        that.showMessage(txtAry);
    });

    socket.on('nick', function (data) {
        var color = Color.hsb(
            data.message.color.h,
            data.message.color.s,
            data.message.color.b
        );

        that.showMessage([
            {message: data.message["old"], color: color},
            {message: " changed its name to ", color: Color.black},
            {message: data.message["new"], color: color}
        ]);
    });
}

