762.source:
function updateTarget(target) {
    if (target) {
        this.updateSliderColor(target.getColor());
        this.widthSlider.setValue(target.getPencilSize()-1);
        this.borderStyleList.setSelection(target.getPencilStyle());
    } else {
        this.updateSliderColor(Color.white);
        this.widthSlider.setValue(0);
        this.borderStyleList.setSelection("solid");
    }
}

