845.source:
function createTitleBarButton(name, tooltip,
                targetObject, targetState, toggleFunction) {
    var titleBar = this.owner.titleBar,
        size = titleBar.getExtent().y - 2,
        b = new lively.morphic.Button(),
        buttonCount;

    if (!this.owner) {
        // when whiteboard is not completely serialized
        // e.g. when loading it from the PartsBin
        return;
    }
    b.whiteboard = this;
    b.targetObject = targetObject;
    b.targetState = targetState;
    b.toggleFunction = toggleFunction;
    b.setToolTip(tooltip);
    b.extraButton = true;
    b.setExtent(pt(size, size));
    if (b.beToolbarButton) {
        b.beToolbarButton(name);
    } else {
        b.setLabel(name);
    }
    b.addScript(function ensurePressed() {
        if (this.isActive) {
            this.isPressed = true;
            this.changeAppearanceFor(true);
        }
    });
    b.addScript(function ensureDepressed() {
        this.isPressed = false;
        this.changeAppearanceFor(false);
    });
    b.addScript(function onFire() {
        this.targetObject[this.toggleFunction].apply(this.targetObject);
    });
    connect(b, "fire", b, "onFire");
    //redefine behaviour on mouse events
    b.onMouseDown = function (evt) {
        if (this.isValidClick(evt) && this.isActive) {
            this.isPressed = true;
            this.changeAppearanceFor(true);
        }
        return false;
    };
    b.onMouseOut = function (evt) {
        //this.isPressed && this.changeAppearanceFor(false);
    };
    b.onMouseUp = function (evt) {
        if (this.isValidClick(evt) && this.isPressed) {
            var newValue = this.toggle ? !this.value : false;
            this.setValue(newValue);
            var targetState = this.targetObject[this.targetState];
            this.changeAppearanceFor(targetState);
            this.isPressed = targetState;
        }
        return false;
    };
    b.openInWorld();
    titleBar.addMorphFront(b);
    buttonCount = titleBar.submorphs.select(function (ea) {
        return ea.extraButton;
    }).length;
    b.setPosition(pt((buttonCount + 1) * b.getExtent().x, 1));
    b.adjustForNewBounds();
    return b;
}

