function setChannel(channelName) {
    var that = this;

    this.channelName = channelName;
    // TODO: maybe we don't want this for proper offline/online switching
    // reasons.
    this.submorphs.each(function (ea) {
        ea.identifier = undefined;
    });

    if (this.isOnline()) {
        // this.submorphs.each(function (ea) {
            // ea.remove();
        // });
        this.socket.emit(
            'channel',
            this.channelName,
            function (join, joinChannel, leave, leaveChannel, initialStrokes,
                     initialMorphs, initialChanges) {
                if (join) {
                    alertOK('Successfully joined channel ' +
                            joinChannel + '.');
                    that.initialSync(
                        initialStrokes,
                        initialMorphs,
                        initialChanges
                    );
                    that.startStepping(1000, 'observeChanges');
                }
                if (leave) {
                    alertOK('Successfully left channel ' + leaveChannel + '.');
                }
            }
        );
    }
}

