909.source:
function initialSync(initialStrokes, initialMorphs,
                    initialChanges) {
    var that = this,
        m,
        existingIds;

    alertOK('Synchronizing with channel.');
    // sync new strokes and morphs
    this.submorphs.each(function (ea) {
        var color,
            line;

        if (ea.identifier === undefined) {
            ea.identifier = ea.id;
            if (ea.isStroke) {
                color = {
                    r: ea.getBorderColor().r * 255,
                    g: ea.getBorderColor().g * 255,
                    b: ea.getBorderColor().b * 255,
                    a: ea.getBorderColor().a
                };
                line = that.packLine(
                    ea.shape.vertices(),
                    ea.timestamp,
                    ea.identifier,
                    color,
                    ea.getBorderWidth(),
                    ea.getBorderStyle()
                );
                that.sendLine(line);
            } else {
                that.sendMorph(ea, false, true);
            }
        }
    });
    this.oldSubmorphs = this.submorphs.pluck('identifier');
    // pull strokes and morphs that are not already there
    existingIds = this.submorphs.pluck('identifier');
    initialStrokes.each(function (ea) {
        that.drawLine(ea);
    });
    initialMorphs.each(function (ea) {
        if (ea.morph && existingIds.indexOf(ea.identifier) < 0) {
            m = that.deserializeMorph(ea.morph);
            that.addMorph(m);
            m.setPosition(pt(ea.position.x, ea.position.y));
        }
    });
    initialChanges.each(function (ea) {
        that.applyChanges(ea, initialMorphs.pluck('identifier'));
    });
}

913.source:
function lineTo(point, optTransform, optColor) {
    var stroke,
        color,
        line;

    if (optTransform) {
        point = this.transformPoint(point);
    }
    stroke = this.getCurrentStroke(point);
    if (optColor) {
        color = optColor;
        stroke.setBorderColor(color);
    } else {
        color = this.color;
    }
    stroke.setVertices(stroke.vertices().concat([point]));
    if (this.isOnline()) {
        line = this.packLine(
            stroke.identifier,
            point,
            {
                r: color.r * 255,
                g: color.g * 255,
                b: color.b * 255,
                a: color.a
            },
            stroke.getBorderWidth(),
            stroke.getBorderStyle()
        );
        this.sendLine(line);
    }
}

