981.source:
function createStrokeShape(points, optSpec) {
    var pointArray,
        spec,
        stroke;

    if (points.constructor !== Array) {
        pointArray = [pt(points.x, points.y)];
    } else {
        pointArray = points;
    }
    if (optSpec === undefined) {
        spec = {
            timestamp: Date.now(),
            color: this.getColor(),
            width: this.getPencilSize(),
            style: this.getPencilStyle()
        };
    } else {
        spec = optSpec;
        spec.color = Color.rgba(
            optSpec.color.r,
            optSpec.color.g,
            optSpec.color.b,
            optSpec.color.a
        );
    }
    stroke = Morph.makePolygon(
        pointArray,
        1,
        spec.color,
        Color.white.withA(0.0001)
    );
    stroke.setBorderWidth(spec.width);
    stroke.setBorderStyle(spec.style);
    stroke.disableEvents();
    stroke.disableDropping();
    stroke.isStroke = true;
    if (spec.identifier !== undefined) {
        stroke.identifier = spec.identifier;
    } else {
        stroke.identifier = stroke.id;
    }
    stroke.time = spec.timestamp;
    return stroke;
}

985.source:
function connectSocket() {
    var resource = 'socket.io',
        socketIOpath = '/socket.io.js',
        socketioURL = 'http://lively-kernel.org:4000/' +
                      resource +
                      socketIOpath,
        source;

    // load Socket.io library if required
    if (window.io === undefined) {
        source = JSLoader.getSync(socketioURL);
        window.eval(source);
    }

    // connect to server
    this.socket = io.connect(
        'lively-kernel.org:4000',
        {
            'force new connection': true,
            'reconnection delay': 1000
        }
    );

    this.createSocketListeners();
}

