function deleteMorphWith(identifier) {
    var toDelete = this.submorphs.find(function (ea) {
        return (ea.identifier === identifier);
    });
    // remove toDelete from oldSubmorphs ary
    if (!toDelete) {
        return;
    }
    this.oldSubmorphs = this.oldSubmorphs.select(function (ea) {
        return toDelete.identifier !== ea;
    });
    toDelete.remove();
}

993.source:
function createSocketListeners() {
    var that = this;

    // auth / (dis)connect + channel join
    this.socket.on('connect', function () {
        that.setUserName(that.getUserName());   // TODO somehow looks strange
        that.setChannel(that.getChannel());     // TODO somehow looks strange
    });

    this.socket.on('error', function (reason) {
        alert('Unable to connect socket.io on namespace ' +
              that.getChannel() + ': ' + reason);
        that.deactivate();
    });

    // client (dis)connects
    this.socket.on('clientConnect', function (data) {
        var dataColor = Color.hsb(data.message.color.h,
                                  data.message.color.s,
                                  data.message.color.b),
            color = data.message.color ? dataColor : Color.black;
        that.chat.showMessage([
            {message: data.message.nick, color: color},
            {message: ' connected', color: Color.black}
        ]);
    });

    this.socket.on('clientDisconnect', function (data) {
        var dataColor = Color.hsb(data.message.color.h,
                                  data.message.color.s,
                                  data.message.color.b),
            color = data.message.color ? dataColor : Color.black;
        that.chat.showMessage([
            {message: data.message.nick, color: color},
            {message: ' disconnected', color: Color.black}
        ]);
        that.owner.submorphs.each(function (ea) {
            if (ea.isMouseIndicator && ea.identifier === data.message.id) {
                ea.remove();
            }
        });
    });

    // ping pong example
    this.socket.on('ping', function (data) {
        that.socket.emit('pong');
    });

    this.socket.on('pong', function (data) {
        alertOK(data.message);
    });

    this.chat.createChatListeners();

    this.socket.on('draw', function (data) {
        that.drawLine(data.message);
    });

    this.socket.on('clear', function () {
        that.submorphs.invoke('remove');
    });

    this.socket.on('delete', function (data) {
        that.deleteMorphWith(data.message);
    });

    this.socket.on('morph', function (data) {
        that.addMorphFrom(data.message);
    });

    this.socket.on('mouse', function (data) {
        that.updateMouseIndicators(data);
    });

    this.socket.on('change', function (data) {
        that.applyChanges(data.message);
    });
}

997.source:
function addMorphFrom(message) {
    var that = this,
        indicatorId = message.id,
        morph,
        indicator;

    if (message.serialized) {
        morph = that.deserializeMorph(message.serialized);
        that.addMorph(morph);
    } else {
        indicator = this.owner.submorphs.find(function (ea) {
            return ea.identifier === indicatorId;
        });
        if (!indicator) {
            return;
        }

        indicator.submorphs.each(function (ea) {
            that.addMorph(ea);
            ea.setPosition(pt(message.position.x, message.position.y));
        });
    }
}

