function deserializeMorph(JSONmorph) {
    var serializer = ObjectGraphLinearizer.forNewLivelyCopy(),
        jso = JSON.parse(JSONmorph.morph),
        morph = serializer.deserializeJso(jso);
    morph.withAllSubmorphsDo(function () {
        if (typeof this.setNewId === 'function') {
            this.setNewId();
        }
    });
    return morph;
}

1021.source:
function applyChanges(diffL, optInitialMorphIds) {
    var that = this,
        toRemove,
        morphChange,
        morph;

    if (diffL.removed) {
        toRemove = this.withAllSubmorphsDetect(function (ea) {
            return ea.identifier === diffL.removed;
        });
        if (toRemove) {
            toRemove.remove();
        }
    } else {
        Properties.own(diffL).each(function (ea) {
            if (diffL[ea] === 'null') {
                return false;
            }
            morphChange = lively.persistence.Serializer.deserialize(diffL[ea]);
            morph = that.withAllSubmorphsDetect(function (m) {
                return m.identifier === ea || m.id === ea;
            });

            if (!morph) {
                return false;
            }

            morph.applyModifications(morphChange.modified, true);
            that.fakeCopy(morph.identifier);

            that.applySubmorphOrderChanges(
                morph,
                morphChange.removed,
                morphChange.added,
                optInitialMorphIds
            );
            morph.refreshRenderContext();
        });
    }
}

1025.source:
function fakeCopy(optIdentifiers) {
    this.prevState = this.submorphs.collect(function (ea) {
        var m,
            identifierIndex;

        identifierIndex = optIdentifiers ?
                            optIdentifiers.indexOf(ea.identifier) :
                            -1;

        if (ea.isStroke) {
            return false;
        }
        if (!optIdentifiers || identifierIndex >= 0) {
            m = ea.copy(['attributeConnections']);
            m.identifier = ea.identifier;
            m.setName(ea.name);
            return m;
        }
        // TODO what is returned in this case here?
    }).select(function (ea) {
        return ea;
    });
}

1029.source:
function observeChanges() {
    var that = this,
        diffL = {},
        remove = false,
        diffLNotEmpty;

    if (this.prevState === undefined) {
        this.fakeCopy();
    }

    this.prevState && this.prevState.each(function (sub) {
        var world = lively.morphic.World.current(),
            match = that.submorphs.find(function (ea) {
                return (sub.identifier === ea.identifier);
            }),
            tmpDiffList,
            identifierHoldingMorph,
            morphInHand;

        if (match) {
            tmpDiffList = sub.twoWayDiff(
                match,
                that.doNotSerializeList.concat(['identifier'])
            );
            if (tmpDiffList &&
                    tmpDiffList.isEmpty &&
                    !tmpDiffList.isEmpty()) {
                Properties.own(tmpDiffList).each(function (ea) {
                    var prop = tmpDiffList[ea];
                    if (prop.isEmpty && !prop.isEmpty()) {
                        diffL[prop.matchingId] = prop;
                        diffL[prop.matchingId].flattenPropertyTree();
                    }
                });
            }
        } else if (!sub.isStroke) {
            identifierHoldingMorph = that.owner.submorphs.find(function (ea) {
                var identifierIndex = ea.submorphs.pluck('identifier').indexOf(
                    sub.identifier
                );
                return (ea.isMouseIndicator && identifierIndex >= 0);
            });
            morphInHand = world.firstHand().submorphs.find(function (ea) {
                return ea.identifier === sub.identifier;
            });
            if (!identifierHoldingMorph && !morphInHand) {
                diffL = sub.identifier;
                remove = true;
            }
        }
    });
    diffLNotEmpty = Properties.own(diffL).find(function (ea) {
        return diffL[ea].isEmpty && !diffL[ea].isEmpty();
    });
    if (diffLNotEmpty) {
        that.sendChange(diffL, remove);
    }
    this.fakeCopy.bind(this).delay(0);
}

1033.source:
function sendChange(change, optRemoved) {
    var that = this,
        serializedChange = {};

    if (this.isOnline()) {
        if (optRemoved) {
            serializedChange.removed = change;
        } else {
            Properties.own(change).each(function (ea) {
                if (Properties.own(change[ea].removed).length > 0) {
                    that.sendMorph(
                        {identifier: that.socket.socket.sessionid},
                        true
                    );
                }
            });
            Properties.own(change).each(function (ea) {
                var serializer = lively.persistence.Serializer;
                serializedChange[ea] = serializer.serialize(change[ea]);
            });
        }
        this.socket.emit('change', {
            message: serializedChange,
            me: false,
            broadcast: true
        });
    }
}

1037.source:
function createMouseIndicator(id, colorCode, nick) {
    var world = lively.morphic.World.current(),
        indicator = Morph.makeRectangle(
            rect(world.getExtent().subXY(10, 10), world.getExtent())
        );

    indicator.identifier = id;
    indicator.isMouseIndicator = true;
    indicator.disableDropping();
    if (colorCode) {
        indicator.setFill(Color.hsb(colorCode.h, colorCode.s, colorCode.b));
    }
    indicator.setToolTip(nick);
    indicator.moveOnBorderFrom = function (ownerMorph, position) {
        var extent = ownerMorph.getExtent(),
            original = true;
        if (position.x > extent.x) {            // TODO refactor magic numbers
            position.x = extent.x;
            original = false;
        } else if (position.x < 0) {
            position.x = 0;
            original = false;
        }
        if (position.y > extent.y) {
            position.y = extent.y;
            original = false;
        } else if (position.y < 21) {
            position.y = 21;
            original = false;
        }
        this.setPosition(position);
        if (original) {
            this.submorphs.invoke('setVisible', true);
        } else {
            this.submorphs.invoke('setVisible', false);
        }
    };
    this.owner.addMorph(indicator);
    return indicator;
}

1041.source:
function updateMouseIndicators(data) {
    var that = this,
        position = pt(data.message.x, data.message.y)
                        .subPt(this.getScrollBounds().topLeft())
                        .addPt(this.getPosition()),
        indicator = this.owner.submorphs.find(function (ea) {
            return ea.identifier === data.message.id;
        }) || this.createMouseIndicator(
            data.message.id,
            data.message.color,
            data.message.nick
        );

    if (data.message.remove) {
        indicator.remove();
        return;
    }

    indicator.moveOnBorderFrom(this, position);

    if (data.message.morphs) {
        data.message.morphs.each(function (ea) {
            var subMorph;
            if (ea.morph) {
                subMorph = that.deserializeMorph(ea.morph);
            } else {
                subMorph = that.withAllSubmorphsDetect(function (each) {
                    return ea.identifier === each.identifier;
                });
            }
            if (!that.prevState) {
                that.prevState = [];
            }
            that.prevState = that.prevState.select(function (each) {
                return each.identifier !== subMorph.identifier;
            });
            indicator.addMorphBack(subMorph);
            // self repairing system could apply a submorph position here
        });
    }
}

