function sendMorph(aMorph, doNotAddToWhiteboard, optSerialize) {
    if (this.isOnline()) {
        this.socket.emit('morph', {
            message: {
                morphId: aMorph.identifier,
                doNotAddToWhiteboard: doNotAddToWhiteboard,
                serialized: optSerialize && aMorph.serialize()
            },
            me: false,
            broadcast: true
        });
        this.enteredBefore = false;
    }
    if (this.prevState !== undefined) {
        if (this.submorphs.length !== this.prevState.length) {
            this.fakeCopy();
        }
    }
}

