function handleStartEvent(evt) {
    var world = lively.morphic.World.current(),
        morphs = world.morphsContainingPoint(evt.getPosition()),
        whiteboardIndex = morphs.indexOf(this);
    if (whiteboardIndex < 0) {
        return false;
    }
    // TODO what is the implication of this?
    // all morphs behind the whiteboard will be ignored in the following check
    // what does array position mean?
    morphs.splice(whiteboardIndex);
    if (!morphs.find(function (ea) { return !ea.isStroke; }) &&
            world.firstHand().submorphs.length <= 0) {
        this.isDrawing = true;
        evt.stop();
        return true;
    }
    return false;
}

