1069.source:
function handleOutEvent(evt) {
    var world = lively.morphic.World.current();
    if (world.morphsContainingPoint(evt.getPosition()).indexOf(this) < 0) {
        this.handleEndEvent(evt);
        if (this.isOnline()) {
            this.enteredBefore = false;
            this.socket.emit('mouse', {
                message: {
                    x: evt.getPosition().x,
                    y: evt.getPosition().y,
                    remove: world.firstHand().submorphs.slice(1).collect(
                        function (ea) {
                            return ea.identifier;
                        }
                    )
                },
                me: false,        // happens on client
                broadcast: true  // get send to other clients
            });
            world.firstHand().withAllSubmorphsDo(function (ea) {
                ea.identifier = undefined;
            });
        }
    }
}

1073.source:
function moveMorphInHand(point) {
    //sync mouse position
    var that = this,
        world = lively.morphic.World.current(),
        data = {x: point.x,
                y: point.y},
        submorphsOfHand = world.firstHand().submorphs.slice(1);

    if (submorphsOfHand.length > 0) { // sync new morph in hand
        if (this.enteredBefore) {// morph was in hand before
            data.isNewMorphPosition = true;
            data.offset = {
                x: submorphsOfHand[0].getPosition().x,
                y: submorphsOfHand[0].getPosition().y
            };
            data.ids = submorphsOfHand.pluck('identifier');
        } else { // morph must be given to hand
            this.enteredBefore = true;
            data.morphs = submorphsOfHand.map(function (ea) {
                var info = ea.identifier ?
                            {} : {morph: that.serializeMorph(ea)};
                return Object.merge(
                    [
                        info,
                        {identifier: ea.identifier},
                        {offset: {
                            x: ea.getPosition().x,
                            y: ea.getPosition().y
                        }}
                    ]
                );
            });
        }
    }

    this.socket.emit('mouse', {
        message: data,
        me: false,
        broadcast: true
    });
}

1077.source:
function serializeMorph(morph) {
    var serialized;

    morph.withAllSubmorphsDo(function (ea) {
        ea.identifier = ea.id;
    });
    if (morph.selectionMorph) {
        disconnect(morph, "extent", morph.selectionMorph, "setExtent");
        disconnect(morph, "_Position", morph.selectionMorph, "setPosition");
        disconnect(morph, "_Rotation", morph.selectionMorph, "setRotation");
        disconnect(morph, "_Scale", morph.selectionMorph, "setScale");
    }
    serialized = morph.serialize(this.doNotSerializeList);
    if (morph.selectionMorph) {
        connect(morph, "extent", morph.selectionMorph, "setExtent");
        connect(morph, "_Position", morph.selectionMorph, "setPosition");
        connect(morph, "_Rotation", morph.selectionMorph, "setRotation");
        connect(morph, "_Scale", morph.selectionMorph, "setScale");
    }
    return serialized;
}

1081.source:
function applySubmorphOrderChanges(morph, toAdd, toRemove,
                    optInitialMorphIds) {
    var that = this,
        alreadyOnBoard,
        toRemoveIdentifier;

    Properties.own(toAdd).each(function (submorphId) {
        var morphId = toAdd[submorphId].identifier;
        if (optInitialMorphIds && optInitialMorphIds.indexOf(morphId) < 0) {
            return;
        }
        alreadyOnBoard = that.withAllSubmorphsDetect(function (ea) {
            return ea.identifier === toAdd[submorphId].identifier;
        });
        if (alreadyOnBoard) {
            morph.addMorph(alreadyOnBoard);
        } else {
            morph.addMorph(toAdd[submorphId]);
        }
    });
    Properties.own(toRemove).each(function (toRemoveId) {
        toRemoveIdentifier = toRemove[toRemoveId].identifier;
        morph.submorphs.find(function (toRemoveMorph) {
            if (toRemoveMorph.identifier === toRemoveIdentifier) {
                toRemoveMorph.remove();
            }
        });
    });
}

1085.source:
function loggedIn(callback) {
    var that = this,
        world = lively.morphic.World.current(),
        wr,
        dummy;

    if (!world.requestUserName()) {
        wr = new WebResource(Config.userNameURL);
        dummy = {
            doItWith: function (input) {
                callback.apply(that, input);
            }
        };
        connect(wr, "status", dummy, "doItWith", {
            converter: function (status) {
                return [status];
            }
        });
        wr.beAsync();
        wr.post();
        return false;
    }
    callback.apply(this, [{isDone: function () { return true; }}]);
    return true;
}

