function drawLine(line) {
    var stroke = this.submorphs.find(function (ea) {
            return line.identifier === ea.identifier;
        }),
        vector = line.vector;
    if (!stroke) {
        stroke = this.createStrokeShape(vector, line);
        this.addMorph(stroke);
    }
    if (!(vector instanceof Array)) {
        vector = [pt(vector.x, vector.y)];
    }
    debugger;
    stroke.setVertices(
        stroke.vertices().concat(
            vector.map(function (ea) {
                return pt(ea.x, ea.y);
            })
        )
    );
}

