function fill(lineNumber) {
    var status, color;
    status = this.itemList[lineNumber];
    switch (status) {
    case 'new':
        color = Color.rgb(255,255,255);
        this.owner.dateOfDone = undefined;
        this.owner.requestedReleaseDate = "...";
        this.owner.estimatedReleaseDate = "...";
        this.setDateProperties();
        break;
    case 'accepted':
        color = Color.blue;
        if (!this.owner.isUpdated && this.selection != "accepted") {
            this.owner.requestedReleaseDate = this.getTimeFromNow("In wie vielen Tagen soll die Implementierung abgeschlossen sein? [Bitte Zahl angeben]?");
            this.setDateProperties();
        };
        break;
    case 'in progress':
        color = Color.rgb(255,255,0)
        if (!this.owner.isUpdated && this.selection != "in progress") {
            this.owner.estimatedReleaseDate = this.getTimeFromNow("Wie viele Tage wird die Implementierung dauern [Bitte Zahl angeben]?");
            this.setDateProperties();
        }; 
        break;
    case 'done':
        color = Color.rgb(0,255,0)
        this.owner.dateOfDone = new Date;
        break;
    case 'rejected':
        color = Color.red;
        break;
    case 'postponed':
        color = Color.rgb(200,200,200);
        break;
    default:
        color = Color.rgb(200,200,200);
        break;
    }
    this.owner.get('Rectangle').shape.setFill(color);
}

