function serialize() {
    if(this.serializationValid){
        return this.serializedObject;
    }else{
      this.serializedObject = {
        title: this.filterTitle(),
        description: this.filterDescription(),
        borderColor: {r: this.shape.getBorderColor().r, g: this.shape.getBorderColor().g, b: this.shape.getBorderColor().b},
        borderWidth: this.shape.getBorderWidth(),
        color: {r: this.shape.getFill().r, g: this.shape.getFill().g, b: this.shape.getFill().b},
        titleColor: {r: this.filterTitleColor().r, g: this.filterTitleColor().g, b: this.filterTitleColor().b},
        descriptionColor: {r: this.filterDescriptionColor().r, g: this.filterDescriptionColor().g, b: this.filterDescriptionColor().b},
        status: this.get('DropDownList').selectedLineNo,
        type: "task",
        creationDate: this.dateOfCreation,
        doneDate: this.dateOfDone,
        estimatedReleaseDate: this.estimatedReleaseDate,
        requestedReleaseDate: this.requestedReleaseDate,
        number: this.number.value
      }

      if(this.associatedWindow.state==="expanded") {        
        this.serializedObject.position = {x:this.associatedWindow.getPosition().x, y: this.associatedWindow.getPosition().y};
        this.serializedObject.extent = {x:this.associatedWindow.getExtent().x, y: this.associatedWindow.getExtent().y}
      }
      if(this.associatedWindow.state==="collapsed") {        
        this.serializedObject.position = {x:this.associatedWindow.expandedPosition.x, y: this.associatedWindow.expandedPosition.y};
        this.serializedObject.extent = {x:this.associatedWindow.expandedExtent.x, y: this.associatedWindow.expandedExtent.y}
      }


      if(this.databaseID!==undefined) {
        this.serializedObject._id = this.databaseID;
      }
      if(this.databaseRev!==undefined) {
        this.serializedObject._rev = this.databaseRev;
      }
        this.serializationValid = true;
        return this.serializedObject;        
    }
}

