function publishUserStory() {
    module('apps.CouchDB').load(true);
    var currentDatabase = this.getCurrentDatabase();
    if(currentDatabase){
        var db = new CouchDB(this.getCurrentDatabase());
        var newtask = this.serialize();
        var result = db.save(newtask);

        if(result.error=="conflict"){
            var databaseDocument = db.open(this.databaseID);
    	   var window = $world.openPartItem("UserStoryDiffViewer","PartsBin/Collaboration");
            window.get("DiffViewer").makeDiff(databaseDocument,this);
        }else{
            this.databaseID = result.id;
            this.databaseRev = result.rev;
            alertOK('task published');
            this.originalProperties = newtask;
        }
        this.updateUserStoryBoards();
    };

    this.updateChangeIndicator();
}

