function deleteUserStory() {
    module('apps.CouchDB').load(true)
    var currentDatabase = this.getCurrentDatabase();
    if(currentDatabase){
        var db = new CouchDB(currentDatabase);
        var docs = db.allDocs()
        var that=this;
        var myRow = docs.rows.select(function(ea) {return ea.id == that.databaseID})
        var myTask = myRow.collect(function(ea) { return db.open(ea.id) })

        myTask.each(function (ea) {
            db.deleteDoc(ea);
            alert("task deleted");
        })
        this.owner.remove();
        this.updateUserStoryBoards();
    }
}

