function setSerializedProperties(props) {
        this.onShutdown();
        this.isUpdated = true; //FIXME refactor: move to stack trace

        this.get('TitlePane').textString = props.title;
        this.get('DescriptionPane').textString = props.description;

        if(this.associatedWindow.state==="expanded") {
            this.associatedWindow.setPosition(pt(props.position.x, props.position.y));
            this.associatedWindow.setExtent(pt(props.extent.x, props.extent.y));
        }
        if(this.associatedWindow.state==="collapsed") {
            this.associatedWindow.expandedPosition = (pt(props.position.x, props.position.y));
            this.associatedWindow.expandedExtent = (pt(props.extent.x, props.extent.y));
        }

        this.shape.setBorderColor(Color.rgb(props.borderColor.r*255, props.borderColor.g*255, props.borderColor.b*255)); 
        this.shape.setBorderWidth(props.borderWidth);
        this.shape.setFill(Color.rgb(props.color.r*255, props.color.g*255, props.color.b*255));
        this.get('TitlePane').shape.setFill(Color.rgb(props.titleColor.r*255, props.titleColor.g*255, props.titleColor.b*255));
        this.get('DescriptionPane').shape.setFill(Color.rgb(props.descriptionColor.r*255, props.descriptionColor.g*255, props.descriptionColor.b*255));
        this.databaseID = props._id;
        this.databaseRev = props._rev;
        this.get('DropDownList').selectAt(props.status);
        this.get('DropDownList').fill(props.status);
        this.dateOfCreation = props.creationDate;
        this.dateOfDone = props.doneDate;
        this.estimatedReleaseDate = props.estimatedReleaseDate;
        this.requestedReleaseDate = props.requestedReleaseDate;
        if (typeof props.number == "undefined"){
            this.number.setValue(0)}
        else {  
            this.number.setValue(props.number)}
        this.setDateProperties();

        this.isUpdated = false;
        this.originalProperties = this.serialize();

        this.updateChangeIndicator();
}

