function serializePropertyAsString(property,serializedObject) {
    if(!serializedObject) {
        serializedObject = this.serialize();
    }

    if(serializedObject[property] != null) {
        var result = serializedObject[property];
        if(result && typeof result === "object"){
            var temp = "(";
            for (var element in result){
                if(result.hasOwnProperty(element)){
                    temp += result[element]+",";
                }
            }
            result = temp.substring(0,temp.length-1)+")";
        }
        return String(result);
    }

}

