function onFire() {
        //refactor: move to DatabaseList
        var selectedDatabase = this.owner.get("DatabaseList").getSelectedDatabaseName();
        module('apps.CouchDB').load(true);
        var db = new CouchDB(selectedDatabase);
        var docs = db.allDocs();
        var allObjs = docs.rows.collect(function(ea) { return db.open(ea.id) });
        var allUserStories = allObjs.select( function(ea) {return ea.type == 'task'});

        var worldUserStories = this.owner.getAllUserStories();
        var that = this;
        allUserStories.each(function(ea) {
            if(!that.owner.showStatus[ea.status]){
                return;
            }
            var myUserStory;
            var existingUserStory = worldUserStories.select(function (eaExist) {
                return eaExist.databaseID == ea._id;
            });
            if(existingUserStory.length){
                myUserStory = existingUserStory[0];
                if(myUserStory.databaseRev != ea._rev) {
                    if (myUserStory.hasChanged()) {
                        alert("Refresh failed for userstory \"" + 
                            myUserStory.filterTitle() + 
                            "\" due to local changes. Please merge the userstory and try again.");
                        that.makeDiff(ea, myUserStory);
                    }
                    else {
                        myUserStory.setSerializedProperties(ea);
                    }
                }
                worldUserStories = worldUserStories.select(function(eaSel) { return (eaSel !== myUserStory) });
            }
            else {
                myUserStory = $world.openPartItem("UserStory", "PartsBin/Collaboration");
                if(myUserStory.setSerializedProperties) {
                    myUserStory.setSerializedProperties(ea);
                } else {
                    if(myUserStory.targetMorph.setSerializedProperties) {
                        myUserStory.targetMorph.setSerializedProperties(ea);
                    }
                }
            }        
        });
        worldUserStories.each(function(ea) {
            if(ea.isPublished()) // don't delete my new unpublished UserStories
                ea.owner.remove();
        })
        this.owner.get("UserStoryProgressBar").updateProgress();
        this.owner.get("List").getUserStories();
}

