function activate() {
    var selectedDatabase = this.owner.get("DatabaseList").getSelectedDatabaseName();
    this.active=true;
    if(!this.db || !this.db.name !== selectedDatabase){
        this.db = new CouchDB(selectedDatabase);
    }
    var that=this;
    this.db.startPollingChanges(function(change){
        if(typeof change === "object"){
            var document = that.db.open(change.id);
            var userStory = that.owner.getUserStoryById(change.id);
            if(change.deleted === true && userStory){
                userStory.owner.remove();
            }
            else{
                var document = that.db.open(change.id);
                if(!userStory && that.owner.showStatus[document.status]){
                    userStory = $world.openPartItem("UserStory","PartsBin/Collaboration").targetMorph;
                }
                if(!userStory.hasChanged()){
                    userStory.setSerializedProperties(document);
                    if(!that.owner.showStatus[document.status]){
                      userStory.owner.remove();
                    }
                }
            }
            that.owner.get("List").getUserStories();
        }
    },function(e){
        that.change();
        alert(e);
        throw(e);
    }); 
}

