function updateProgress() {
    var worldUserStories = this.owner.getAllUserStories();
    var newStories = 0;
    var inprogress = 0;
    var done = 0;
    worldUserStories.each(function(ea) {
        switch (ea.get('DropDownList').selection) {
        case 'done':
            done++;
            break;
        case 'new':
            newStories++;
            break;
        default:
            inprogress++;
        }
    });

    this.updateBar((newStories/(newStories+done+inprogress)),(done/(newStories+done+inprogress)));
    this.setLabel(String(newStories)+"+"+String(inprogress)+"+"+String(done)+"/"+String(done+inprogress+newStories));

}

