function updateBar(left, right) {
    var maxExt = this.getExtent();
    var width = Math.floor(maxExt.x * left);
    this.progressLeft.setVisible(width > 1);
    this.progressLeft.setExtent(pt(width, maxExt.y));
    if (typeof right !== 'undefined') {
        width = Math.floor(maxExt.x * right);
        this.progressRight.setVisible(width > 1);
        this.progressRight.setExtent(pt(width, maxExt.y));
    } else {
        this.progressRight.setVisible(false)}
}

