function getUserStories(stat) {
    var status = this.currentStatus;
    if(stat !== undefined && stat !== false)
        status = stat;
    this.currentStatus = status;        


    this.updateList();
    var that = this;

    var sortFunction = function(a,b) {
        return (a.serialize().title.toLowerCase() < b.serialize().title.toLowerCase()) ? -1 : 1;
    };

    for(i in status) {
    	if(status.hasOwnProperty(i)) {
            var stories =  this.owner.getUserStoriesByStatus(status[i]).sort(sortFunction);
            for(var j=0; j<stories.length; j++) {
                    that.addItem(stories[j].get("TitlePane").textString);
            }
        }
    }
}

