function getAllUserStories() {
    var userStories = [];
    $world.submorphs.each(function(ea) {
        if(ea.partsBinMetaInfo && ea.partsBinMetaInfo.partName === "UserStory") {
            if(ea.targetMorph) {
                userStories.push(ea.targetMorph);
            } else {
                userStories.push(ea);
            }
        }
    });

    return userStories;
}

