function accept() {
    var list = this.get("PropertyList");
    var property = list.getSelectedItem();
    var allProperties = list.getList();
    var number = list.selectedLineNo;

    property = String(property).replace(/ \(changed\)/,"");
    this.finalVersion[property] = this.newData[property];

    if(this.hasChanged(property)){
        property+=" (changed)";
    }
    allProperties[number]=property;
    list.updateList(allProperties);   
}

